<node TEXT="&lt;_html&gt;&#xa;&lt;body&gt;&#xa;&lt;%&#xa;   &#xa;       String[] nombres = new String[6];&#xa;        nombres[0]= &quot;Pepe Pérez&quot;;&#xa;        nombres[1]= &quot;Pepe Lopez&quot;;&#xa;        nombres[2]= &quot;Juan Rodrigo&quot;;&#xa;        nombres[3]= &quot;Tomás Garcia&quot;;&#xa;        nombres[4]= &quot;Ángel Dominguez&quot;;&#xa;        nombres[5]= &quot;Ramón Guzman&quot;;&#xa;      &#xa;        int[] ids = new int[6];&#xa;        ids[0]= 17;&#xa;        ids[1]= 8;&#xa;        ids[2]= 4;&#xa;        ids[3]= 71;&#xa;        ids[4]= 52;&#xa;        ids[5]= 47;&#xa;    %&gt;&#xa;   &#xa;    &lt;select name=&quot;idPersona&quot; &gt;&#xa;        &lt;option value=&quot;-1&quot; selected&gt;(ELIJA...)&lt;/option&gt;&#xa;       &#xa;        &lt;% for (int i=0; i&lt;ids.length; i++) { %&gt;&#xa;            &lt;option value=&quot;&lt;%=ids[i]%&gt;&quot;&gt;&lt;%=nombres[i]%&gt;&lt;/option&gt;&#xa;        &lt;% } %&gt;&#xa;&lt;/select&gt;&#xa;&#xa;&lt;/body&gt;&#xa;&lt;/html&gt;" ID="ID_309258026" CREATED="1571129955930" MODIFIED="1571129959647"/>
</node>
