<node TEXT="&lt;?php&#xa;&#xa;// Tres formas diferentes de cargar el array:&#xa;&#xa;$listaNombres = array(&quot;Pepe Pérez&quot;, &quot;Pepe López&quot;, &quot;Tomás García&quot;);&#xa;$listaNombres[48] = &quot;Hola&quot;;&#xa;&#xa;//$listaNombres[&quot;17&quot;] = &quot;Pepe Pérez&quot;;&#xa;//$listaNombres[&quot;8&quot;] = &quot;Pepe López&quot;;&#xa;//$listaNombres[&quot;14&quot;] = &quot;Tomás García&quot;;&#xa;&#xa;//$listaNombres = [&#xa;//    17 =&gt; &quot;Pepe Pérez&quot;,&#xa;//    8 =&gt; &quot;Pepe López&quot;,&#xa;//    4 =&gt; &quot;Pepe López&quot;,&#xa;//    71 =&gt; &quot;Tomás García&quot;,&#xa;//    52 =&gt; &quot;Ángel Domínguez&quot;,&#xa;//    47 =&gt; &quot;Ramón Guzmán&quot;&#xa;//];&#xa;?&gt;&#xa;&#xa;&#xa;&#xa;&lt;html&gt;&#xa;&#xa;&lt;head&gt;&#xa;    &lt;meta charset=&apos;UTF-8&apos;&gt;&#xa;&lt;/head&gt;&#xa;&#xa;&lt;body&gt;&#xa;&lt;select name=&apos;idPersona&apos;&gt;&#xa;    &lt;option value=&apos;-1&apos;&gt;ELIJA&lt;/option&gt;&#xa;    &lt;?php&#xa;        foreach ($listaNombres as $clave =&gt; $valor):&#xa;            echo &quot;&lt;option value=&apos;$clave&apos;&gt;$valor&lt;/option&gt;\n&quot;;&#xa;        endforeach;&#xa;    ?&gt;&#xa;&lt;/select&gt;&#xa;&lt;/body&gt;&#xa;&#xa;&lt;/html&gt;" ID="ID_1683407528" CREATED="1575287378203" MODIFIED="1575287456037"/>
