<node TEXT="&lt;%&#xa;   int annoInicio = 1900; // Prueba a poner aquí 1903.&#xa;   int annoFin = 2019; // Prueba a poner aquí 2023.&#xa;  &#xa;   boolean ajustadoOrden = false;&#xa;   if (annoInicio &gt; annoFin) { // Si van al revés, los intercambiamos.&#xa;        int aux = annoInicio;&#xa;        annoInicio = annoFin;&#xa;        annoFin = aux;&#xa;   &#xa;        ajustadoOrden = true;&#xa;   }&#xa;  &#xa;   boolean ajustadoInicio = false;&#xa;   if (annoInicio % 10 != 0) { // Truncamos año de inicio a la década inferior.&#xa;        annoInicio = annoInicio / 10; // Pierde el posible decimal que queda al final (la unidad).&#xa;        annoInicio = annoInicio * 10; // Recuperamos la magnitud original pero con el último dígito a cero.&#xa;&#xa;        ajustadoInicio = true;&#xa;   }&#xa;  &#xa;   boolean ajustadoFin = false;&#xa;   if ((annoFin+1) % 10 != 0) { // Truncamos año de inicio a la década inferior.&#xa;        // annoFin no necesita ajuste por la forma en que está programado el for.&#xa;   &#xa;        // Pero sí queremos informar al usuario:&#xa;        ajustadoFin = true;&#xa;   }&#xa;%&gt;&#xa;&#xa;&lt;%@ page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot; %&gt;&#xa;   &#xa;&lt;html&gt;&#xa;&#xa;&lt;head&gt;&#xa;    &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;&lt;/head&gt;&#xa;   &#xa;&lt;body&gt;&#xa;   &#xa;    &lt;% if (ajustadoOrden) { %&gt;&#xa;       &lt;p&gt;El año de inicio era superior al de fin. Se ha reinterpretado invirtiendo su orden.&lt;/p&gt;&#xa;    &lt;% } %&gt;&#xa;    &lt;% if (ajustadoInicio) { %&gt;&#xa;       &lt;p&gt;El año de inicio no correspondía con el inicio de la década. Se ha reinterpretado tomando la década entera.&lt;/p&gt;&#xa;    &lt;% } %&gt;&#xa;    &lt;% if (ajustadoFin) { %&gt;&#xa;       &lt;p&gt;El año de fin no correspondía con el final de la década. Se ha reinterpretado tomando la década entera.&lt;/p&gt;&#xa;    &lt;% } %&gt;&#xa;   &#xa;   &#xa;   &#xa;    &lt;ul&gt;&#xa;        &lt;% for (int decada = annoInicio; decada &lt;= annoFin; decada = decada+10) { %&gt;&#xa;            &lt;li&gt;&#xa;                &lt;a href=&apos;008-annos.jsp?decada=&lt;%=decada%&gt;&apos;&gt;&#xa;                    Ver los años de la década de &lt;%=decada%&gt;.&#xa;                &lt;/a&gt;&#xa;            &lt;/li&gt;&#xa;        &lt;% } %&gt;&#xa;    &lt;/ul&gt;&#xa;&lt;/body&gt;&#xa;   &#xa;&lt;/html&gt;" ID="ID_1618164387" CREATED="1571210898402" MODIFIED="1571210903332"/>
</node>
<node TEXT="008-annos.jsp" FOLDED="true" ID="ID_959275766" CREATED="1571210904935" MODIFIED="1571210928666">
<node TEXT="&lt;%&#xa;   int decada = -1;&#xa;&#xa;   boolean errorParametroNoValido = false;&#xa;   try{&#xa;        decada = Integer.valueOf(request.getParameter(&quot;decada&quot;));&#xa;   } catch (Exception e) {&#xa;      errorParametroNoValido = true;&#xa;   }&#xa;&#xa;   boolean errorDecadaFueraRango = false;&#xa;   if (decada &lt; 1900 || decada &gt; 2019) {&#xa;      errorDecadaFueraRango = true;&#xa;   }&#xa;&#xa;   boolean hayErrores = (errorParametroNoValido || errorDecadaFueraRango);&#xa;&#xa;   boolean reinterpretacionRedondeoDecada = false;&#xa;   if (decada % 10 != 0) { // Truncamos la deada (es decir, el año de inicio) a la década inferior.&#xa;      decada = decada / 10; // Pierde el posible decimal que queda al final (la unidad).&#xa;      decada = decada * 10; // Recuperamos la magnitud original pero con el último dígito a cero.&#xa;&#xa;      reinterpretacionRedondeoDecada = true;&#xa;   }&#xa;%&gt;&#xa;&#xa;&lt;%@ page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot; %&gt;&#xa;&#xa;&lt;html&gt;&#xa;&#xa;&lt;head&gt;&#xa;    &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;&lt;/head&gt;&#xa;&#xa;&lt;body&gt;&#xa;  &lt;% if (hayErrores) { %&gt;&#xa;   &#xa;      &lt;% if (errorParametroNoValido) { %&gt;&#xa;        &lt;p&gt;Se ha recibido un parámetro no válido.&lt;/p&gt;&#xa;      &lt;% } else if (errorDecadaFueraRango) { %&gt;&#xa;        &lt;p&gt;La fecha indicada no entraba dentro de un rango razonable. Se ha ignorado.&lt;/p&gt;&#xa;      &lt;% } else { assert (false); } // Nunca deberíamos llegar el else. %&gt;&#xa;&#xa;  &lt;% } else { %&gt;&#xa;   &#xa;      &lt;% if (reinterpretacionRedondeoDecada) { %&gt;&#xa;        &lt;p&gt;El año de inicio no correspondía con el inicio de la década. Se ha reinterpretado tomando la década desde el principio.&lt;/p&gt;&#xa;      &lt;% } %&gt;&#xa;   &#xa;      &lt;ul&gt;&#xa;        &lt;% for (int anno = decada; anno &lt; (decada+10); anno++) { %&gt;&#xa;          &lt;li&gt;&lt;a href=&quot;https://www.google.com/search?q=&lt;%=anno%&gt;&quot;&gt;Buscar sobre el año &lt;%=anno%&gt;.&lt;/a&gt;&lt;/li&gt;&#xa;        &lt;% } %&gt;&#xa;      &lt;/ul&gt;&#xa;           &#xa;  &lt;% } %&gt;&#xa;&lt;/body&gt;&#xa;       &#xa;&lt;/html&gt;" ID="ID_581935055" CREATED="1571210913758" MODIFIED="1571210915865"/>
</node>
