<node TEXT="&lt;%@ page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot;%&gt;&#xa;&#xa;&lt;html&gt;&#xa;&#xa;&lt;head&gt;&#xa;  &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;  &lt;title&gt;Formulario&lt;/title&gt;&#xa;&lt;/head&gt;&#xa;&#xa;&lt;body&gt;&#xa;  &lt;form action=&quot;calculadora-resultado.jsp&quot;&gt;&#xa;   &lt;h1&gt;Calculadora&lt;/h1&gt;&#xa;    &lt;p&gt;&lt;input type=&quot;number&quot; name=&quot;operando1&quot;&gt;&lt;/p&gt;&#xa;    &lt;select name=&quot;operacion&quot;&gt;&#xa;      &lt;option value=&quot;sum&quot;&gt;Suma&lt;/option&gt;&#xa;      &lt;option value=&quot;res&quot;&gt;Resta&lt;/option&gt;&#xa;      &lt;option value=&quot;mul&quot;&gt;Multiplicación&lt;/option&gt;&#xa;      &lt;option value=&quot;div&quot;&gt;Division&lt;/option&gt;&#xa;    &lt;/select&gt;&#xa;&#xa;    &lt;p&gt;&lt;input type=&quot;number&quot; name=&quot;operando2&quot;&gt;&lt;/p&gt;&#xa;    &lt;p&gt;&lt;input type=&quot;submit&quot; value=&quot;Enviar&quot;&gt;&lt;/p&gt;&#xa;  &lt;/form&gt;&#xa;&lt;/body&gt;&#xa;&#xa;&lt;/html&gt;" ID="ID_66124877" CREATED="1571733535703" MODIFIED="1571733549926"/>
</node>
<node TEXT="calculadora-resultado.jsp" FOLDED="true" ID="ID_1085712635" CREATED="1381175040541" MODIFIED="1571733533511">
<node TEXT="&lt;%&#xa;  int operando1 = Integer.parseInt(request.getParameter(&quot;operando1&quot;));&#xa;  int operando2 = Integer.parseInt(request.getParameter(&quot;operando2&quot;));&#xa;  String codigoOperacion = request.getParameter(&quot;operacion&quot;);&#xa;&#xa;  int resultado = 0;&#xa;  String nombreOperacion = null;&#xa;&#xa;  boolean errorDivCero = false;&#xa;  boolean errorOperacionNoValida = false;&#xa;&#xa;  if (codigoOperacion.equals(&quot;sum&quot;)) {&#xa;    nombreOperacion = &quot;suma&quot;;&#xa;    resultado = operando1+operando2;&#xa;  } else if(codigoOperacion.equals(&quot;res&quot;)) {&#xa;    nombreOperacion = &quot;resta&quot;;&#xa;    resultado = operando1-operando2;&#xa;   } else if (codigoOperacion.equals(&quot;mul&quot;)) {&#xa;    nombreOperacion = &quot;multiplicación&quot;;&#xa;    resultado = operando1*operando2;&#xa;  } else if (codigoOperacion.equals(&quot;div&quot;)) {&#xa;    nombreOperacion = &quot;división&quot;;&#xa;    if (operando2 != 0) {&#xa;      resultado = operando1/operando2;&#xa;    } else {&#xa;      errorDivCero = true;&#xa;    }&#xa;  } else {&#xa;    errorOperacionNoValida = true;&#xa;  }&#xa;%&gt;&#xa;&#xa;&#xa;&#xa;&lt;%@ page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot;%&gt;&#xa;&#xa;&lt;html&gt;&#xa;&#xa;&lt;head&gt;&#xa;  &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;  &lt;title&gt;Resultado&lt;/title&gt;&#xa;&lt;/head&gt;&#xa;&#xa;&lt;body&gt;&#xa;&#xa;&lt;%&#xa;  if (errorDivCero) {&#xa;    out.println(&quot;&lt;p&gt;Se ha solicitado dividir entre 0 y no es posible.&lt;/p&gt;&quot;);&#xa;  } else if (errorOperacionNoValida) {&#xa;    out.println(&quot;&lt;p&gt;Se ha solicitado realizar una operación no reconocida.&lt;/p&gt;&quot;);&#xa;  } else { // CASO NORMAL&#xa;%&gt;&#xa;&#xa;  &lt;p&gt;El resultado de realizar una &lt;%=nombreOperacion%&gt; entre &lt;%=operando1%&gt; y &lt;%=operando2%&gt; es &lt;%=resultado%&gt;.&lt;/p&gt;&#xa;&#xa;&lt;%&#xa;  }&#xa;%&gt;&#xa;&#xa;&lt;/body&gt;&#xa;&#xa;&lt;/html&gt;" ID="ID_751021846" CREATED="1571734883477" MODIFIED="1603274138309"/>
</node>
</node>
</node>
