<node TEXT="&lt;%&#xa;  int acumulado = 0; // Por defecto, para cuando no haya formulario enviado.&#xa;  int diferencia = 1; // Por defecto, para cuando no haya formulario enviado.&#xa;&#xa;  boolean formularioEnviado = (request.getParameter(&quot;acumulado&quot;) != null &amp;&amp; request.getParameter(&quot;diferencia&quot;) != null);&#xa;  boolean quierenSumar = (request.getParameter(&quot;sumar&quot;) != null);&#xa;  boolean quierenRestar = (request.getParameter(&quot;restar&quot;) != null);&#xa;  if (formularioEnviado &amp;&amp; (quierenSumar || quierenRestar)) {&#xa;    diferencia = Integer.valueOf(request.getParameter(&quot;diferencia&quot;));&#xa;    if (quierenSumar) {&#xa;      acumulado = Integer.valueOf(request.getParameter(&quot;acumulado&quot;)) + diferencia;&#xa;    } else {&#xa;      acumulado = Integer.valueOf(request.getParameter(&quot;acumulado&quot;)) - diferencia;&#xa;    }&#xa;  }&#xa;%&gt;&#xa;&#xa;&#xa;&#xa;&lt;%@ page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot;%&gt;&#xa;&#xa;&lt;html&gt;&#xa;&#xa;&lt;head&gt;&#xa;  &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;&lt;/head&gt;&#xa;&#xa;&lt;body&gt;&#xa; &#xa;&lt;h1&gt;&lt;%=acumulado%&gt;&lt;/h1&gt;&#xa;&#xa;&lt;form method=&quot;get&quot;&gt;&#xa;  &lt;input type=&quot;hidden&quot; name=&quot;acumulado&quot; value=&quot;&lt;%=acumulado%&gt;&quot;&gt;&#xa;  &lt;input type=&quot;number&quot; name=&quot;diferencia&quot; value=&quot;&lt;%=diferencia%&gt;&quot;&gt;&#xa;  &lt;input type=&quot;submit&quot; name=&quot;sumar&quot; value=&quot;Sumar&quot;&gt;&#xa;  &lt;input type=&quot;submit&quot; name=&quot;restar&quot; value=&quot;Restar&quot;&gt;&#xa;  &lt;input type=&quot;submit&quot; name=&quot;resetear&quot; value=&quot;Resetear más complicado&quot;&gt;&#xa;&lt;/form&gt;&#xa;&#xa;&lt;a href=&quot;ejercicio.jsp&quot;&gt;Resetear más simple (preferible)&lt;/a&gt;&#xa;&#xa;&lt;/body&gt;