<node TEXT="JSP solución con lo básico pero&#xa;a falta de la mayoría de opcionales" ID="ID_721027558" CREATED="1413828468589" MODIFIED="1572347757359">
<icon BUILTIN="z_script"/>
<node TEXT="adivinar-inicio.jsp" FOLDED="true" ID="ID_1012947944" CREATED="1413828471721" MODIFIED="1572345641421">
<node TEXT="&lt;%@ page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot;%&gt;&#xa;&#xa;&lt;html&gt;&#xa;&#xa;&lt;head&gt;&#xa;  &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;&lt;/head&gt;&#xa;&#xa;&lt;body&gt;&#xa;&#xa;&lt;h1&gt;ADIVINAR EL NÚMERO&lt;/h1&gt;&#xa;&#xa;&lt;form action=&quot;adivinar-principal.jsp&quot; method=&quot;get&quot;&gt;&#xa;  &lt;p&gt;Jugador 1: Introduce un número:&lt;/p&gt;&#xa;  &lt;input type=&quot;text&quot; name=&quot;oculto&quot; /&gt;&#xa;  &lt;input type=&quot;submit&quot; value=&quot;Guardar&quot; /&gt;&#xa;&lt;/form&gt;&#xa;&#xa;&lt;/body&gt;&#xa;&#xa;&lt;/html&gt;" ID="ID_954933606" CREATED="1573030239164" MODIFIED="1573030244333"/>
</node>
<node TEXT="adivinar-principal.jsp" FOLDED="true" ID="ID_170309619" CREATED="1413828475410" MODIFIED="1572345646884">
<node TEXT="&lt;%&#xa;  int oculto = Integer.parseInt(request.getParameter(&quot;oculto&quot;));&#xa;&#xa;  int intento = -1;&#xa;  boolean hayIntento = false;&#xa;  if (request.getParameter(&quot;intento&quot;) != null) {&#xa;    intento = Integer.parseInt(request.getParameter(&quot;intento&quot;));&#xa;    hayIntento = true;&#xa;  }&#xa;%&gt;&#xa;&#xa;&#xa;&#xa;&lt;%@ page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot;%&gt;&#xa;&#xa;&lt;html&gt;&#xa;&#xa;&lt;head&gt;&#xa;  &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;&lt;/head&gt;&#xa;&#xa;&lt;body&gt;&#xa;&#xa;&lt;h1&gt;ADIVINA EL NÚMERO&lt;/h1&gt;&#xa;&#xa;&#xa;&lt;%&#xa;  if (hayIntento) {&#xa;    String cercania = &quot;&quot;;&#xa;  &#xa;    int asteriscos = 1 + Math.abs(intento - oculto) / 10;&#xa;    for (int i=1; i &lt;= asteriscos; i++) {&#xa;      cercania = cercania + &quot;*&quot;;&#xa;    }&#xa; &#xa;    if (intento &lt; oculto) {&#xa;      out.println(&quot;&lt;p&gt;El número que buscas es mayor (&quot; + cercania + &quot;)&lt;/p&gt;&quot;);&#xa;    } else if (intento &gt; oculto) {&#xa;      out.println(&quot;&lt;p&gt;El número que buscas es menor (&quot; + cercania + &quot;)&lt;/p&gt;&quot;);&#xa;    } else {&#xa;      out.println(&quot;&lt;p&gt;¡Has adivinado el número!&lt;/p&gt;&quot;);&#xa;    }&#xa;  }&#xa;&#xa;  if (intento != oculto) { // Presentamos el formulario:&#xa;%&gt;&#xa;&#xa;&lt;form method=&quot;get&quot;&gt;&#xa;  &lt;p&gt;Jugador 2: Adivina el número:&lt;/p&gt;&#xa;  &lt;input type=&quot;hidden&quot; name=&quot;oculto&quot; value=&quot;&lt;%=oculto%&gt;&quot;&gt;&#xa;  &lt;input type=&quot;text&quot; name=&quot;intento&quot;&gt;&#xa;  &lt;input type=&quot;submit&quot; value=&quot;Intentar&quot;&gt;&#xa;&lt;/form&gt;&#xa;&#xa;&lt;%&#xa;  }&#xa;%&gt;&#xa;&#xa;&lt;/body&gt;&#xa;&lt;/html&gt;" ID="ID_772970747" CREATED="1573030254773" MODIFIED="1573030257469"/>
</node>
</node>
<node TEXT="JSP solución alumno&#xa;con record, aleatorio..." ID="ID_1214735576" CREATED="1573030261154" MODIFIED="1573030416749">
<icon BUILTIN="z_script"/>
<node TEXT="inicio.jsp" FOLDED="true" ID="ID_977669556" CREATED="1573030286576" MODIFIED="1573030288803">
<node TEXT="&lt;%@page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot;%&gt;&#xa;&lt;%&#xa;String record= request.getParameter(&quot;record&quot;);&#xa;Integer recordGlobal;&#xa;if(record==null){&#xa;    recordGlobal=10;&#xa;}else{&#xa;    recordGlobal=Integer.valueOf(record);&#xa;}&#xa;&#xa;Integer aleatorio=(int)Math.floor(1+100*Math.random());&#xa;%&gt;&#xa;&#xa;&lt;!DOCTYPE html&gt;&#xa;&lt;html lang=&quot;en&quot;&gt;&#xa;&lt;head&gt;&#xa;&lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;&lt;meta name=&quot;viewport&quot; content=&quot;width=device-width, initial-scale=1.0&quot;&gt;&#xa;&lt;meta http-equiv=&quot;X-UA-Compatible&quot; content=&quot;ie=edge&quot;&gt;&#xa;    &lt;title&gt; Title &lt;/title&gt;&#xa;&lt;/head&gt;&#xa;ici&#xa;&lt;body&gt;&#xa;        &lt;h1&gt;${param.error}&lt;/h1&gt;&#xa;        &lt;h1&gt;Listo para jugar a &quot;Adivinar el numero&quot;&lt;/h1&gt;&#xa;        &lt;h2&gt;Pulsa el boton para generar un numero aleatorio y tratar de adivinarlo (entre 0 y 100)&lt;/h2&gt;&#xa;        &lt;form action=&quot;juego.jsp&quot; method=&quot;GET&quot;&gt;&#xa;        &lt;input type=&quot;hidden&quot; name=&quot;numeroAdivinar&quot; value=&quot;&lt;%=aleatorio%&gt;&quot;&gt;&#xa;        &lt;input type=&quot;hidden&quot; name=&quot;record&quot; value=&quot;&lt;%=recordGlobal%&gt;&quot;&gt;&#xa;        &lt;input type=&quot;submit&quot; value=&quot;Jugar&quot;&gt;&#xa;        &lt;/form&gt;&#xa;&lt;/body&gt;&#xa;&lt;/html&gt;" ID="ID_267995396" CREATED="1573030298191" MODIFIED="1573030303278"/>
</node>
