<node TEXT="&lt;%@page language=&quot;java&quot; contentType=&quot;text/html; charset=UTF-8&quot; pageEncoding=&quot;UTF-8&quot;%&gt;&#xa;&#xa;&lt;%&#xa;&#xa;Integer esAdivinado=0;&#xa;&#xa;Integer recordActual=0;&#xa;Integer recordGlobal;&#xa;String recordRecogido= request.getParameter(&quot;record&quot;);&#xa;recordGlobal=Integer.valueOf(recordRecogido);&#xa;&#xa;&#xa;boolean nombresIntroducidos=false;&#xa;&#xa;Double proximidad=0.0;&#xa;Integer diferencia=0;&#xa;&#xa;Integer numeroIntentos=0;&#xa;String numeroIntentosRecogido=request.getParameter(&quot;numeroIntentos&quot;);&#xa;&#xa;String numeroAdivinarRecogido= request.getParameter(&quot;numeroAdivinar&quot;);&#xa;Integer numeroAdivinar=0;&#xa;&#xa;String intentoRecogido= request.getParameter(&quot;numeroIntroducido&quot;);&#xa;Integer intentoActual;&#xa;&#xa;&#xa;&#xa;if(numeroAdivinarRecogido==null || numeroAdivinarRecogido==&quot;&quot;){&#xa;&#xa;String mensajeError= &quot;El parametro enviado está fuera de los limites&quot;;&#xa;String redirectURL = &quot;inicio.jsp?error=&quot;+ mensajeError;&#xa;              response.sendRedirect(redirectURL);&#xa;&#xa;}else{&#xa;numeroAdivinar= Integer.valueOf(numeroAdivinarRecogido);&#xa;&#xa;if(numeroAdivinar&lt;0 || numeroAdivinar&gt;100){&#xa;String mensajeError= &quot;El parametro enviado está fuera de los limites&quot;;&#xa;String redirectURL = &quot;http://localhost:8080/examples/dwes/juego/inicio.jsp?error=&quot;+ mensajeError;&#xa;              response.sendRedirect(redirectURL);&#xa;}&#xa;}&#xa;&#xa;&#xa;&#xa;&#xa;if(intentoRecogido== null || intentoRecogido== &quot;&quot;){&#xa;&#xa;intentoActual=0;&#xa;numeroIntentos=0;&#xa;&#xa;}else{&#xa;&#xa;intentoActual= Integer.valueOf(intentoRecogido);&#xa;&#xa;&#xa;if(numeroIntentosRecogido==null || numeroIntentosRecogido==&quot;&quot;){&#xa;&#xa;numeroIntentos=0;&#xa;&#xa;}else{&#xa;numeroIntentos= Integer.valueOf(numeroIntentosRecogido);&#xa;}&#xa;}&#xa;&#xa;if(intentoActual&gt;numeroAdivinar){&#xa;diferencia=intentoActual-numeroAdivinar;&#xa;out.print(&quot;El numero que queremos adivinar es menor&quot;);&#xa;out.print(&quot;&lt;br&gt;Llevas: &quot; + numeroIntentos + &quot; intentos de 10&quot;);&#xa;numeroIntentos= numeroIntentos+1;&#xa;&#xa;if(diferencia&gt;=0 &amp;&amp; diferencia&lt;=10){&#xa;proximidad=0.7;&#xa;}else if(diferencia&gt;10 &amp;&amp; diferencia&lt;=20){&#xa;proximidad=0.5;&#xa;}else if(diferencia&gt;20 &amp;&amp; diferencia&lt;=30){&#xa;proximidad=0.2;&#xa;}else{&#xa;proximidad=0.0;&#xa;}&#xa;&#xa;&#xa;}else if(intentoActual&lt;numeroAdivinar){&#xa;diferencia=numeroAdivinar-intentoActual;&#xa;out.print(&quot;El numero que queremos adivinar es mayor&quot;);&#xa;out.print(&quot;&lt;br&gt;Llevas: &quot; + numeroIntentos + &quot; intentos de 10&quot;);&#xa;numeroIntentos= numeroIntentos+1;&#xa;&#xa;if(diferencia&gt;=0 &amp;&amp; diferencia&lt;=10){&#xa;proximidad=0.7;&#xa;}else if(diferencia&gt;10 &amp;&amp; diferencia&lt;=20){&#xa;proximidad=0.5;&#xa;}else if(diferencia&gt;20 &amp;&amp; diferencia&lt;=30){&#xa;proximidad=0.2;&#xa;}else{&#xa;proximidad=0.0;&#xa;}&#xa;&#xa;&#xa;&#xa;}else{&#xa;esAdivinado=1;&#xa;recordActual= numeroIntentos;&#xa;&#xa;}&#xa;&#xa;%&gt;&#xa;&#xa;&lt;!DOCTYPE html&gt;&#xa;&lt;html lang=&quot;en&quot;&gt;&#xa;&lt;head&gt;&#xa;&lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=utf-8&quot;/&gt;&#xa;&lt;meta name=&quot;viewport&quot; content=&quot;width=device-width, initial-scale=1.0&quot;&gt;&#xa;&lt;meta http-equiv=&quot;X-UA-Compatible&quot; content=&quot;ie=edge&quot;&gt;&#xa;&lt;title&gt; Title &lt;/title&gt;&#xa;&lt;/head&gt;&#xa;&#xa;&lt;body&gt;&#xa;&lt;% if(esAdivinado==0){&#xa;if(numeroIntentos&lt;=10){ %&gt;&#xa;&#xa;&#xa;&lt;form action=juego.jsp&gt;&#xa;        &lt;label&gt;ADIVINA EL NUMERO&lt;/label&gt;&#xa;        &lt;input type=&quot;text&quot; name=&quot;numeroIntroducido&quot;&gt;&#xa;        &lt;input type=&quot;hidden&quot; name=&quot;numeroAdivinar&quot; value=&quot;&lt;%=numeroAdivinar%&gt;&quot;&gt;&#xa;        &lt;input type=&quot;hidden&quot; name=&quot;numeroIntentos&quot; value=&quot;&lt;%=numeroIntentos%&gt;&quot;&gt;&#xa;        &lt;input type=&quot;hidden&quot; name=&quot;record&quot; value=&quot;&lt;%=recordGlobal%&gt;&quot;&gt;&#xa;        &lt;input type=&quot;submit&quot;&gt;&#xa;        &lt;/form&gt;&#xa;        &lt;meter value=&quot;&lt;%=proximidad%&gt;&quot;&gt;&lt;/meter&gt;&#xa;         &lt;% }else{ %&gt;&#xa;         &lt;h1&gt;Intentos acabados! El numero era &lt;%=numeroAdivinar%&gt;&lt;/h1&gt;&#xa;        &lt;form action=inicio.jsp&gt;&#xa;        &lt;input type=&quot;submit&quot; value=&quot;Reintentar&quot;&gt;&#xa;        &lt;input type=&quot;hidden&quot; name=&quot;record&quot; value=&quot;&lt;%=recordGlobal%&gt;&quot;&gt;&#xa;     &lt;/form&gt;&#xa;&#xa;         &lt;% }&#xa;         }else{ %&gt;&#xa;        &lt;h1&gt;Enhorabuena lo adivinaste! El numero era &lt;%=numeroAdivinar%&gt;&lt;/h1&gt;&#xa;        &lt;h2&gt;Lo completaste en &lt;%=numeroIntentos%&gt; intento(s) por tanto tu puntuacion es de &lt;%=(10-numeroIntentos)%&gt;&lt;/h2&gt;&#xa;        &lt;%if(recordActual&lt;recordGlobal){&#xa;        %&gt;&#xa;        &lt;h2&gt;Ademas superaste el anterior record de &lt;%=recordGlobal%&gt; con un nuevo record de &lt;%=recordActual%&gt;&lt;/h2&gt;&#xa;     &lt;%recordGlobal=recordActual;}%&gt;&#xa;        &lt;form action=inicio.jsp&gt;&#xa;        &lt;input type=&quot;submit&quot; value=&quot;Volver a jugar&quot;&gt;&#xa;        &lt;input type=&quot;hidden&quot; name=&quot;record&quot; value=&quot;&lt;%=recordGlobal%&gt;&quot;&gt;&#xa;     &lt;/form&gt;&#xa;       &#xa;         &lt;% } %&gt;&#xa;&lt;/body&gt;&#xa;&lt;/html&gt;" ID="ID_1034532318" CREATED="1573030304963" MODIFIED="1573030409429"/>
